INSERT INTO `#__jlike_reminders` (`state`, `title`, `days_before`, `email_template`, `subject`, `content_type`) VALUES
(1, 'There is 1 day left until this assignment expires', 1, "<p>Hi {name},</p><p> </p><p>Course entitled {content_title} had been assigned to you some time ago. With less than {days_before} days to go, please complete it as soon as possible. Kindly note that it is a mandatory course, and needs to be closed it at the earliest. The due date to complete the course is {content_due_date}.</p><p> </p><p>Below is the course link, please copy and paste it in your browser's address bar to access the content</p><p> </p><p>{content_link}</p><p> </p><p>Happy Learning!</p>", 'Your assignment will expire after 1 days', 'com_tjlms.course');
